using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using UnityEngine;

namespace BlockFilter
{
    public static class Sprites
    {
        public static Sprite slopeSprite;
        public static Sprite tiltSprite;
        public static Sprite straightSprite;
        public static Sprite curveSprite;
        public static Sprite sbendSprite;
        public static Sprite xSprite;

        private static string slope64 = "";
        private static string tilt64 = "";
        private static string straight64 = "";
        private static string curve64 = "";
        private static string sbend64 = "";
        private static string x64 = "";
        public static void Initialize()
        {
            slopeSprite = Base64ToSprite(slope64);
            tiltSprite = Base64ToSprite(tilt64);
            straightSprite = Base64ToSprite(straight64);
            curveSprite = Base64ToSprite(curve64);
            sbendSprite = Base64ToSprite(sbend64);
            xSprite = Base64ToSprite(x64);
        }

        public static Sprite Base64ToSprite(string base64)
        {
            // Convert the base64 string to a byte array
            byte[] imageBytes = Convert.FromBase64String(base64);

            // Create a new texture and load the image bytes
            Texture2D texture = new Texture2D(1, 1);
            texture.LoadImage(imageBytes);

            // Create a new sprite using the texture
            Sprite sprite = Sprite.Create(texture, new Rect(0, 0, texture.width, texture.height), Vector2.zero);

            return sprite;
        }
    }
}
